import React, { useEffect, useState } from 'react';
import './modal.css';

function Modal({ movie, status, toggleModal }) {
  const [videoSrc, setVideoSrc] = useState('');

  useEffect(() => {
    if (status) {
      // Get saved time for this movie
      const savedTime = localStorage.getItem(`video-${movie.title}`) || 0;
      
      // Add autoplay & start time params
      const url = movie.video.includes('?')
        ? `${movie.video}&autoplay=1&start=${savedTime}`
        : `${movie.video}?autoplay=1&start=${savedTime}`;

      setVideoSrc(url);

      // Save playback time periodically
      const interval = setInterval(() => {
        const iframe = document.querySelector('iframe');
        if (iframe && iframe.contentWindow) {
          try {
            const currentTime = iframe.contentWindow.location.search.match(/t=(\d+)/);
            if (currentTime) {
              localStorage.setItem(`video-${movie.title}`, currentTime[1]);
            }
          } catch (err) {
            // ignore cross-origin errors
          }
        }
      }, 1000);

      return () => clearInterval(interval);
    } else {
      setVideoSrc('');
    }
  }, [status, movie.video, movie.title]);

  return (
    <div className={`movieModal ${status ? 'active' : ''}`}>
      <a href="#" className="modalClose" onClick={toggleModal}>
        <ion-icon name="close-outline"></ion-icon>
      </a>
      {status && (
        <iframe
          width="1280"
          height="720"
          src={videoSrc}
          title={`${movie.title} | Official Movie`}
          frameBorder="0"
          allow="autoplay; accelerometer; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
          allowFullScreen
        />
      )}
    </div>
  );
}

export default Modal;
