import React, { useState, useRef } from 'react';
import './movieSwiper.css';
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, EffectCoverflow, FreeMode } from 'swiper/modules';

function MovieSwiper({ slides, slideChange }) {
  const [tapPosition, setTapPosition] = useState(null);
  const [swipeOverlay, setSwipeOverlay] = useState(null);
  const swipeStartRef = useRef(null);
  const swipeTimeRef = useRef(null);

  const handleClick = (slideId, e) => {
    slideChange(slideId);

    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.touches ? e.touches[0].clientX - rect.left : e.clientX - rect.left;
    const y = e.touches ? e.touches[0].clientY - rect.top : e.clientY - rect.top;
    setTapPosition({ x, y });
    setTimeout(() => setTapPosition(null), 500);
  };

  const handleTouchStart = (e) => {
    const touch = e.touches[0];
    swipeStartRef.current = { x: touch.clientX, y: touch.clientY };
    swipeTimeRef.current = Date.now();
    setSwipeOverlay({ dx: 0, dy: 0, angle: 0, velocity: 0 });
  };

  const handleTouchMove = (e) => {
    const touch = e.touches[0];
    if (!swipeStartRef.current) return;

    const dx = touch.clientX - swipeStartRef.current.x;
    const dy = touch.clientY - swipeStartRef.current.y;
    const angle = Math.atan2(dy, dx) * (180 / Math.PI);
    const dt = (Date.now() - swipeTimeRef.current) / 1000;
    const distance = Math.sqrt(dx ** 2 + dy ** 2);
    const velocity = distance / dt;

    setSwipeOverlay({ dx, dy, angle, velocity });
  };

  const handleTouchEnd = () => {
    setSwipeOverlay(null);
    swipeStartRef.current = null;
    swipeTimeRef.current = null;
  };

  return (
    <Swiper
      effect="coverflow"
      grabCursor={true}
      centeredSlides={true}
      slidesPerView="auto"
      loop={true}
      autoplay={{ delay: 2500, disableOnInteraction: false }}
      coverflowEffect={{ rotate: 50, stretch: 0, depth: 100, modifier: 1, slideShadows: true }}
      freeMode={{ enabled: true, momentum: true }}
      touchRatio={3}
      threshold={5}
      resistance={true}
      resistanceRatio={0.85}
      modules={[Autoplay, EffectCoverflow, FreeMode]}
      className="movieSwiper"
    >
      {slides.map((slide) => (
        <SwiperSlide key={slide._id}>
          <div
            className="slide-content"
            onTouchStart={handleTouchStart}
            onTouchMove={handleTouchMove}
            onTouchEnd={handleTouchEnd}
            onClick={(e) => handleClick(slide._id, e)}
          >
            <img src={slide.previewImg} alt="Preview" draggable={false} />
            {tapPosition && (
              <div
                className="tap-indicator"
                style={{ left: tapPosition.x, top: tapPosition.y }}
              />
            )}
            {swipeOverlay && (
              <div
                className="swipe-hud"
                style={{
                  transform: `translate(${swipeOverlay.dx}px, ${swipeOverlay.dy}px) rotate(${swipeOverlay.angle}deg)`,
                }}
                title={`Velocity: ${Math.round(swipeOverlay.velocity)}px/s`}
              />
            )}
          </div>
        </SwiperSlide>
      ))}
      <div className="swipe-hint" />
    </Swiper>
  );
}

export default MovieSwiper;
