import React, { useState } from 'react';
import './movieSwiper.css';
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import 'swiper/css/pagination';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, EffectCoverflow } from 'swiper/modules';

function MovieSwiper({ slides, slideChange }) {
  const [tapPosition, setTapPosition] = useState(null);
  const [hoveredIndex, setHoveredIndex] = useState(null);

  // Handle click/tap
  const handleClick = (slideId, e) => {
    slideChange(slideId);

    if (e.type === 'touchstart' || e.touches) {
      const rect = e.currentTarget.getBoundingClientRect();
      setTapPosition({ x: e.clientX - rect.left, y: e.clientY - rect.top });
      setTimeout(() => setTapPosition(null), 500);
    }
  };

  // Handle pointer proximity for desktop & mobile “pre-hover”
  const handlePointerMove = (index, e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const pointerX = e.clientX || (e.touches && e.touches[0].clientX);
    const pointerY = e.clientY || (e.touches && e.touches[0].clientY);
    if (!pointerX || !pointerY) return;

    const distanceX = Math.abs(pointerX - (rect.left + rect.width / 2));
    const distanceY = Math.abs(pointerY - (rect.top + rect.height / 2));
    const distance = Math.sqrt(distanceX ** 2 + distanceY ** 2);

    // Trigger pre-hover if pointer/finger is within 120px
    if (distance < 120) setHoveredIndex(index);
    else if (hoveredIndex === index) setHoveredIndex(null);
  };

  // Remove pre-hover when pointer leaves slide
  const handlePointerLeave = (index) => {
    if (hoveredIndex === index) setHoveredIndex(null);
  };

  return (
    <Swiper
      effect="coverflow"
      grabCursor={true}
      centeredSlides={true}
      slidesPerView={'auto'}
      loop={true}
      autoplay={{ delay: 2500, disableOnInteraction: false }}
      coverflowEffect={{ rotate: 50, stretch: 0, depth: 100, modifier: 1, slideShadows: true }}
      modules={[Autoplay, EffectCoverflow]}
      className="movieSwiper"
      allowTouchMove={true}
      simulateTouch={true}
      touchStartPreventDefault={false}
      shortSwipes={true}
      longSwipes={true}
      touchRatio={1}
      threshold={5}
    >
      {slides.map((slide, index) => (
        <SwiperSlide key={slide._id}>
          <div
            className={`slide-content ${hoveredIndex === index ? 'pre-hover' : ''}`}
            onPointerMove={(e) => handlePointerMove(index, e)}
            onPointerLeave={() => handlePointerLeave(index)}
            onTouchMove={(e) => handlePointerMove(index, e)}
          >
            <img
              src={slide.previewImg}
              alt="Preview"
              draggable={false}
              onClick={(e) => handleClick(slide._id, e)}
              onTouchStart={(e) => handleClick(slide._id, e)}
            />
            {tapPosition && (
              <div
                className="tap-indicator"
                style={{ left: tapPosition.x, top: tapPosition.y }}
              />
            )}
          </div>
        </SwiperSlide>
      ))}
    </Swiper>
  );
}

export default MovieSwiper;
