import React, { useState } from 'react';
import './movieSwiper.css';
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import 'swiper/css/pagination';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, EffectCoverflow } from 'swiper/modules';

function MovieSwiper({ slides, slideChange }) {
  const [tapPosition, setTapPosition] = useState(null);

  return (
    <Swiper
      effect={'coverflow'}
      grabCursor={true}
      centeredSlides={true}
      slidesPerView={'auto'}
      loop={true}
      autoplay={{
        delay: 2500,
        disableOnInteraction: false,
      }}
      coverflowEffect={{
        rotate: 50,
        stretch: 0,
        depth: 100,
        modifier: 1,
        slideShadows: true,
      }}
      modules={[Autoplay, EffectCoverflow]}
      className="movieSwiper"
      onSlideClick={(swiper, e) => {
        const index = swiper.clickedIndex;
        if (index != null) {
          slideChange(slides[index]._id);

          // Tap position for indicator
          const rect = swiper.slides[index].getBoundingClientRect();
          const x = e.type.startsWith('touch') ? e.touches[0].clientX : e.clientX;
          const y = e.type.startsWith('touch') ? e.touches[0].clientY : e.clientY;

          setTapPosition({ x: x - rect.left, y: y - rect.top, slideIndex: index });
          setTimeout(() => setTapPosition(null), 600);
        }
      }}
    >
      {slides.map((slide, idx) => (
        <SwiperSlide key={slide._id}>
          <div className="slide-wrapper">
            <img
              src={slide.previewImg}
              alt="Preview Image"
              draggable={false}
            />
            {tapPosition && tapPosition.slideIndex === idx && (
              <img
                src="/src/handpointing.png"
                className="handpoint-indicator"
                style={{ left: tapPosition.x, top: tapPosition.y }}
                alt="Handpoint"
              />
            )}
          </div>
        </SwiperSlide>
      ))}
    </Swiper>
  );
}

export default MovieSwiper;
