import { useEffect, useState } from 'react';
import './modal.css';

function Modal({ movie, status, toggleModal }) {
  const [visible, setVisible] = useState(status);

  // Handle fade-in/fade-out
  useEffect(() => {
    if (status) {
      setVisible(true); // fade-in
    } else {
      const timeout = setTimeout(() => setVisible(false), 500); // fade-out duration matches CSS
      return () => clearTimeout(timeout);
    }
  }, [status]);

  if (!movie || !visible) return null;

  return (
    <div className={`movieModal ${status ? 'active' : 'fade-out'}`}>
      <button className="modalClose" onClick={toggleModal}>
        <ion-icon name="close-outline"></ion-icon>
      </button>
      <iframe
        width="1280"
        height="720"
        src={movie.video}
        title={`${movie.title} | Official Movie`}
        frameBorder="0"
        allow="accelerometer; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
        allowFullScreen
      />
    </div>
  );
}

export default Modal;
