// MovieSwiper.js
import React, { useRef } from 'react';
import './movieSwiper.css';
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import 'swiper/css/pagination';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, EffectCoverflow } from 'swiper/modules';

function MovieSwiper({ slides, slideChange }) {
  const swiperRef = useRef(null);
  const isDraggingRef = useRef(false);
  const startPosRef = useRef({ x: 0, y: 0 });

  const handleTouchStart = (e) => {
    isDraggingRef.current = false;
    startPosRef.current = {
      x: e.touches ? e.touches[0].clientX : e.clientX,
      y: e.touches ? e.touches[0].clientY : e.clientY
    };
  };

  const handleTouchMove = (e) => {
    if (!startPosRef.current.x) return;
    
    const currentX = e.touches ? e.touches[0].clientX : e.clientX;
    const currentY = e.touches ? e.touches[0].clientY : e.clientY;
    const diffX = Math.abs(currentX - startPosRef.current.x);
    const diffY = Math.abs(currentY - startPosRef.current.y);
    
    // If moved more than 10px, consider it a drag
    if (diffX > 10 || diffY > 10) {
      isDraggingRef.current = true;
    }
  };

  const handleSlideClick = (slideId, e) => {
    // Prevent click if user was dragging
    if (isDraggingRef.current) {
      e.preventDefault();
      e.stopPropagation();
      return;
    }
    
    e.preventDefault();
    e.stopPropagation();
    slideChange(slideId);
  };

  return (
    <Swiper
      effect="coverflow"
      grabCursor={true}
      centeredSlides={true}
      slidesPerView={'auto'}
      loop={true}
      autoplay={{
        delay: 2500,
        disableOnInteraction: false,
        pauseOnMouseEnter: true,
      }}
      coverflowEffect={{
        rotate: 40,
        stretch: 0,
        depth: 120,
        modifier: 1,
        slideShadows: true,
      }}
      modules={[Autoplay, EffectCoverflow]}
      className="movieSwiper"
      allowTouchMove={true}
      simulateTouch={true}
      slideToClickedSlide={false}  // Disabled to prevent interference
      watchSlidesProgress={true}
      passiveListeners={false}     // Changed to false for better click handling
      onSwiper={(swiper) => {
        swiperRef.current = swiper;
      }}
    >
      {slides.map((slide) => (
        <SwiperSlide key={slide._id}>
          <div
            className="slide-content"
            onClick={(e) => handleSlideClick(slide._id, e)}
            onTouchStart={handleTouchStart}
            onTouchMove={handleTouchMove}
            onMouseDown={handleTouchStart}
            onMouseMove={handleTouchMove}
          >
            <img
              src={slide.previewImg}
              alt="Preview"
              draggable={false}
            />
          </div>
        </SwiperSlide>
      ))}
    </Swiper>
  );
}

export default MovieSwiper;
