// MovieSwiper.js - Debug Version
import React from 'react';
import './movieSwiper.css';
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import 'swiper/css/pagination';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, EffectCoverflow, FreeMode } from 'swiper/modules';

function MovieSwiper({ slides, slideChange }) {
    const handleSlideClick = (slideId, event) => {
        console.log('üé¨ Slide clicked!', slideId);
        console.log('Event:', event);
        console.log('slideChange function:', slideChange);
        
        event.preventDefault();
        event.stopPropagation();
        
        if (slideChange && typeof slideChange === 'function') {
            slideChange(slideId);
            console.log('‚úÖ slideChange called with:', slideId);
        } else {
            console.error('‚ùå slideChange is not a function:', slideChange);
        }
    };

    const handleImageClick = (slideId, event) => {
        console.log('üñºÔ∏è Image clicked directly!', slideId);
        handleSlideClick(slideId, event);
    };

    console.log('üé≠ MovieSwiper rendered with slides:', slides?.length);
    console.log('üîß slideChange prop:', slideChange);

    return (
        <Swiper
            effect="coverflow"
            grabCursor={true}
            centeredSlides={true}
            slidesPerView={'auto'}
            loop={true}
            // Temporarily disable these to test basic clicks
            freeMode={false}
            autoplay={false}
            coverflowEffect={{
                rotate: 50,
                stretch: 0,
                depth: 100,
                modifier: 1,
                slideShadows: true,
            }}
            modules={[EffectCoverflow]} // Simplified modules
            className="movieSwiper"
            allowTouchMove={true}
            simulateTouch={true}
            slideToClickedSlide={true}
            onSlideChange={(swiper) => {
                console.log('üìç Slide changed to:', swiper.activeIndex);
            }}
            onClick={(swiper, event) => {
                console.log('üéØ Swiper clicked:', event);
            }}
        >
            {slides?.map((slide, index) => (
                <SwiperSlide 
                    key={slide._id}
                    onClick={(e) => {
                        console.log('üì± SwiperSlide clicked:', slide._id);
                        handleSlideClick(slide._id, e);
                    }}
                >
                    <div 
                        className="slide-content"
                        onClick={(e) => {
                            console.log('üì¶ Slide content clicked:', slide._id);
                            handleSlideClick(slide._id, e);
                        }}
                        style={{
                            // Debug styling to make click area visible
                            border: '2px solid red',
                            cursor: 'pointer'
                        }}
                    >
                        <img
                            src={slide.previewImg}
                            alt={`Preview ${index}`}
                            draggable={false}
                            onClick={(e) => handleImageClick(slide._id, e)}
                            style={{
                                // Debug styling
                                border: '2px solid blue',
                                cursor: 'pointer'
                            }}
                        />
                        {/* Debug text overlay */}
                        <div style={{
                            position: 'absolute',
                            top: '5px',
                            left: '5px',
                            background: 'rgba(255,0,0,0.8)',
                            color: 'white',
                            padding: '2px 5px',
                            fontSize: '12px',
                            borderRadius: '3px'
                        }}>
                            ID: {slide._id}
                        </div>
                    </div>
                </SwiperSlide>
            ))}
        </Swiper>
    );
}

export default MovieSwiper;
