// MovieSwiper.js - WITH CUSTOM CURSOR SUPPORT
import React, { useEffect, useRef, useState } from 'react';
import './movieSwiper.css';
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import 'swiper/css/pagination';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, EffectCoverflow } from 'swiper/modules';

function MovieSwiper({ slides, slideChange }) {
  const swiperRef = useRef(null);
  const [customCursorsLoaded, setCustomCursorsLoaded] = useState(false);

  // Preload custom cursor images
  useEffect(() => {
    const grabCursor = new Image();
    const pointingCursor = new Image();
    
    let grabLoaded = false;
    let pointingLoaded = false;

    grabCursor.onload = () => {
      grabLoaded = true;
      if (pointingLoaded) setCustomCursorsLoaded(true);
    };
    
    pointingCursor.onload = () => {
      pointingLoaded = true;
      if (grabLoaded) setCustomCursorsLoaded(true);
    };

    grabCursor.onerror = () => {
      console.warn('Could not load handgrab.png - using default cursor');
    };
    
    pointingCursor.onerror = () => {
      console.warn('Could not load handpointing.png - using default cursor');
    };

    // Set cursor image paths - adjust these to match your project structure
    grabCursor.src = '/handgrab.png';
    pointingCursor.src = '/handpointing.png';
  }, []);

  // Apply custom cursor classes when images are loaded
  useEffect(() => {
    if (customCursorsLoaded && swiperRef.current) {
      const swiperEl = swiperRef.current;
      swiperEl.classList.add('custom-grab-cursor');
      
      const slides = swiperEl.querySelectorAll('.swiper-slide');
      slides.forEach(slide => {
        slide.classList.add('custom-pointer-cursor');
      });
    }
  }, [customCursorsLoaded]);

  const handleSlideClick = (slideId) => {
    slideChange(slideId);
  };

  return (
    <Swiper
      ref={swiperRef}
      effect="coverflow"
      grabCursor={true}
      centeredSlides={true}
      slidesPerView={'auto'}
      loop={true}
      autoplay={{
        delay: 2500,
        disableOnInteraction: false,
        pauseOnMouseEnter: true,
      }}
      coverflowEffect={{
        rotate: 40,
        stretch: 0,
        depth: 120,
        modifier: 1,
        slideShadows: true,
      }}
      modules={[Autoplay, EffectCoverflow]}
      className="movieSwiper"
      allowTouchMove={true}
      simulateTouch={true}
      touchStartPreventDefault={false}
      shortSwipes={true}
      longSwipes={true}
      touchRatio={1}
      touchAngle={45}
      threshold={5}
      touchEventsTarget="container"
    >
      {slides.map((slide) => (
        <SwiperSlide
          key={slide._id}
          onClick={() => handleSlideClick(slide._id)}
        >
          <div className="slide-content">
            <img
              src={slide.previewImg}
              alt="Preview"
              draggable={false}
            />
          </div>
        </SwiperSlide>
      ))}
    </Swiper>
  );
}

export default MovieSwiper;
