import React, { useState } from 'react';
import './movieSwiper.css';
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, EffectCoverflow } from 'swiper/modules';

function MovieSwiper({ slides, slideChange }) {
  const [tapPosition, setTapPosition] = useState(null);

  const handleClick = (slideId, e) => {
    console.log('Slide clicked:', slideId, e.type); // debug
    slideChange(slideId);

    if (e.touches || e.type === 'touchstart') {
      const rect = e.currentTarget.getBoundingClientRect();
      const x = e.clientX - rect.left;
      const y = e.clientY - rect.top;
      console.log('Tap position:', { x, y }); // debug
      setTapPosition({ x, y });
      setTimeout(() => setTapPosition(null), 500);
    }
  };

  const handleSlideChange = (swiper) => {
    console.log('Swiper active index:', swiper.activeIndex); // debug
  };

  return (
    <Swiper
      effect="coverflow"
      grabCursor={true}
      centeredSlides={true}
      slidesPerView="auto"
      loop={true}
      autoplay={{ delay: 2500, disableOnInteraction: false }}
      coverflowEffect={{ rotate: 50, stretch: 0, depth: 100, modifier: 1, slideShadows: true }}
      freeMode={true}
      freeModeMomentum={true}
      freeModeMomentumVelocityRatio={0.7}
      touchRatio={3.5}
      threshold={10}
      resistance={true}
      resistanceRatio={0.85}
      modules={[Autoplay, EffectCoverflow]}
      className="movieSwiper"
      onSlideChange={handleSlideChange} // debug active index
    >
      {slides.map((slide) => (
        <SwiperSlide key={slide._id}>
          <div className="slide-content">
            <img
              src={slide.previewImg}
              alt="Preview"
              draggable={false}
              onClick={(e) => handleClick(slide._id, e)}
              onTouchStart={(e) => handleClick(slide._id, e)}
            />
            {tapPosition && (
              <div className="tap-indicator" style={{ left: tapPosition.x, top: tapPosition.y }} />
            )}
          </div>
        </SwiperSlide>
      ))}
      <div className="swipe-hint" />
    </Swiper>
  );
}

export default MovieSwiper;
