// MovieSwiper.js
import React from 'react';
import './movieSwiper.css';
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import 'swiper/css/pagination';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, EffectCoverflow, FreeMode } from 'swiper/modules';

function MovieSwiper({ slides, slideChange }) {
    const handleSlideClick = (slideId, event) => {
        // Ensure the click is registered and call slideChange
        event.stopPropagation();
        slideChange(slideId);
    };

    return (
        <Swiper
            effect="coverflow"
            grabCursor={true}
            centeredSlides={true}
            slidesPerView={'auto'}
            loop={true}
            freeMode={true}
            freeModeMomentum={true}
            freeModeMomentumRatio={0.85}
            freeModeSticky={true}
            autoplay={{
                delay: 2500,
                disableOnInteraction: false,
                pauseOnMouseEnter: true, // Pause autoplay on hover for better UX
            }}
            coverflowEffect={{
                rotate: 50,
                stretch: 0,
                depth: 100,
                modifier: 1,
                slideShadows: true,
            }}
            modules={[Autoplay, EffectCoverflow, FreeMode]}
            className="movieSwiper"
            allowTouchMove={true}
            simulateTouch={true}
            touchStartPreventDefault={false}
            slideToClickedSlide={true}
            // Enhanced click handling
            onSlideChange={(swiper) => {
                // Optional: You can add logic here if needed when slide changes
            }}
        >
            {slides.map((slide, index) => (
                <SwiperSlide key={slide._id}>
                    <div 
                        className="slide-content"
                        onClick={(e) => handleSlideClick(slide._id, e)}
                    >
                        <img
                            src={slide.previewImg}
                            alt="Preview"
                            draggable={false} // Prevent image drag interference
                        />
                    </div>
                </SwiperSlide>
            ))}
        </Swiper>
    );
}

export default MovieSwiper;
