import React from 'react';
import './movieSwiper.css';
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import 'swiper/css/free-mode';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, EffectCoverflow, FreeMode } from 'swiper/modules';

function MovieSwiper({ slides, slideChange }) {

  const handleImageLoad = (e, index) => {
    // Staggered cinematic load for posters
    setTimeout(() => e.target.classList.add("loaded"), index * 150);
  };

  const handleClick = (slideId, e) => {
    slideChange(slideId);
  };

  return (
    <div className="movieSwiper">
      <Swiper
        effect="coverflow"
        grabCursor={true}
        centeredSlides={true}
        slidesPerView="auto"
        loop={true}
        autoplay={{ delay: 3000, disableOnInteraction: false }}
        coverflowEffect={{
          rotate: 30,
          stretch: 0,
          depth: 100,
          modifier: 1,
          slideShadows: false,
        }}
        modules={[Autoplay, EffectCoverflow, FreeMode]}
        onSlideChange={slideChange}
      >
        {slides.map((slide, index) => (
          <SwiperSlide key={index}>
            <div className="slide-content">
              <img
                src={slide.previewImg}
                alt={`poster-${index}`}
                onLoad={(e) => handleImageLoad(e, index)}
                onClick={(e) => handleClick(slide._id, e)}
              />
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
}

export default MovieSwiper;
