import React, { useState, useEffect } from 'react';
import './header.css';
import NavListItem from '../components/NavListItem';
import navListData from '../data/navListData';

function Header({ scroll }) {
  const [navList, setNavList] = useState(navListData);
  const [active, setActive] = useState(false); // menu open/closed
  const [icon, setIcon] = useState("nav__toggler");

  // Toggle menu open/close
  const navToggle = () => {
    const newState = !active;
    setActive(newState);

    if (newState) {
      document.body.style.overflow = 'hidden'; // lock scroll
      setIcon("nav__toggler toggle");
    } else {
      document.body.style.overflow = 'auto'; // restore scroll
      setIcon("nav__toggler");
    }
  };

  // Close menu (used for clicking links or overlay)
  const closeMenu = () => {
    setActive(false);
    setIcon("nav__toggler");
    document.body.style.overflow = 'auto';
  };

  // Handle active nav item
  const handleNavOnClick = id => {
    const newNavList = navList.map(nav => {
      nav.active = nav._id === id;
      return nav;
    });
    setNavList(newNavList);
    closeMenu();
  };

  // Cleanup scroll lock on unmount
  useEffect(() => {
    return () => {
      document.body.style.overflow = 'auto';
    };
  }, []);

  return (
    <header className={`${scroll > 100 ? 'scrolled' : ''}`}>
      <a href="/" className="logo">
        Stella Belessi
      </a>

      {/* Navigation Menu */}
      <ul className={`nav__menu ${active ? 'nav__active' : ''}`}>
        {navList.map(nav => (
          <NavListItem
            key={nav._id}
            nav={nav}
            onClick={closeMenu}
            navOnClick={handleNavOnClick}
          />
        ))}
      </ul>

      {/* Overlay (only visible when active) */}
      {active && <div className="nav-overlay" onClick={closeMenu}></div>}

      {/* Hamburger */}
      <div onClick={navToggle} className={icon}>
        <div className="line1"></div>
        <div className="line2"></div>
        <div className="line3"></div>
      </div>
    </header>
  );
}

export default Header;
