import React, { useState } from 'react';
import './header.css';
import NavListItem from '../components/NavListItem';
import navListData from '../data/navListData';

// Header component with scroll detection + mobile nav toggle
function Header({ scroll }) {
  const [navList, setNavList] = useState(navListData);

  const handleNavOnClick = (id) => {
    const newNavList = navList.map((nav) => {
      nav.active = false;
      if (nav._id === id) nav.active = true;
      return nav;
    });
    setNavList(newNavList);
    closeMenu(); // Close menu after clicking a link
  };

  const [active, setActive] = useState("nav__menu");
  const [icon, setIcon] = useState("nav__toggler");

  // Toggle mobile menu
  const navToggle = () => {
    if (active === "nav__menu") {
      setActive("nav__menu nav__active");
      setIcon("nav__toggler toggle");
      document.body.style.overflow = "hidden"; // lock scroll
    } else {
      setActive("nav__menu");
      setIcon("nav__toggler");
      document.body.style.overflow = "auto"; // unlock scroll
    }
  };

  // Force close menu (used on link click)
  const closeMenu = () => {
    setActive("nav__menu");
    setIcon("nav__toggler");
    document.body.style.overflow = "auto"; // always restore
  };

  return (
    <header className={`${scroll > 100 ? "scrolled" : ""}`}>
      <a href="/" className="logo">
        Stella Belessi
      </a>

      <ul className={active}>
        {navList.map((nav) => (
          <NavListItem
            key={nav._id}
            nav={nav}
            onClick={closeMenu}
            navOnClick={handleNavOnClick}
          />
        ))}
      </ul>

      <div
        onClick={navToggle}
        className={icon}
        role="button"
        aria-label="menu toggle"
      >
        <div className="line1"></div>
        <div className="line2"></div>
        <div className="line3"></div>
      </div>
    </header>
  );
}

export default Header;
