import React from 'react';
import './movieSwiper.css';
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import 'swiper/css/pagination';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, EffectCoverflow, FreeMode } from 'swiper/modules';

function MovieSwiper({ slides, slideChange }) {
    return (
        <Swiper
            effect="coverflow"
            grabCursor={true}
            centeredSlides={true}
            slidesPerView={'auto'}
            loop={true}
            freeMode={true}          // ✅ enable smooth physics + bounce
            freeModeMomentum={true}  // ✅ slide continues after release
            freeModeMomentumRatio={0.8} // adjust momentum speed
            autoplay={{
                delay: 2500,
                disableOnInteraction: false,
            }}
            coverflowEffect={{
                rotate: 50,
                stretch: 0,
                depth: 100,
                modifier: 1,
                slideShadows: true,
            }}
            modules={[Autoplay, EffectCoverflow, FreeMode]}
            className="movieSwiper"
            allowTouchMove={true}
        >
            {slides.map(slide => (
                <SwiperSlide key={slide._id}>
                    <img
                        src={slide.previewImg}
                        alt="Preview"
                        onClick={() => slideChange(slide._id)}
                    />
                </SwiperSlide>
            ))}
        </Swiper>
    );
}

export default MovieSwiper;
