import React, { useState, useEffect } from 'react';
import './movieSwiper.css';
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import 'swiper/css/free-mode';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, EffectCoverflow, FreeMode } from 'swiper/modules';

function MovieSwiper({ slides, slideChange }) {
  const [tapPosition, setTapPosition] = useState(null);
  const [cursorPos, setCursorPos] = useState({ x: -100, y: -100 }); // off-screen initially
  const [loadedSlides, setLoadedSlides] = useState({}); // track loaded images

  // Click or touch handler
  const handleClick = (slideId, e) => {
    slideChange(slideId);
    const rect = e.currentTarget.getBoundingClientRect();
    let x, y;

    if (e.type.startsWith("touch")) {
      const touch = e.touches[0];
      x = touch.clientX - rect.left;
      y = touch.clientY - rect.top;
    } else {
      x = e.clientX - rect.left;
      y = e.clientY - rect.top;
    }

    setTapPosition({ x, y });
    setTimeout(() => setTapPosition(null), 500);
  };

  // Cursor follow effect
  useEffect(() => {
    const move = (e) => setCursorPos({ x: e.clientX, y: e.clientY });
    window.addEventListener('mousemove', move);
    return () => window.removeEventListener('mousemove', move);
  }, []);

  // Mark slide as loaded
  const handleImageLoad = (id) => {
    setLoadedSlides(prev => ({ ...prev, [id]: true }));
  };

  return (
    <>
      <Swiper
        modules={[EffectCoverflow, Autoplay, FreeMode]}
        effect="coverflow"
        grabCursor={true}
        centeredSlides={true}
        slidesPerView="auto"
        loop={true}
        loopedSlides={Math.min(slides.length, 10)}
        freeMode={{ enabled: true, momentum: true }}
        autoplay={{ delay: 2500, disableOnInteraction: false }}
        coverflowEffect={{ rotate: 50, stretch: 0, depth: 100, modifier: 1, slideShadows: true }}
        touchRatio={1}
        threshold={5}
        resistance={true}
        resistanceRatio={0.85}
        touchStartPreventDefault={false}
        className="movieSwiper"
      >
        {slides.map(slide => (
          <SwiperSlide key={slide._id}>
            <div
              className="slide-content"
              onClick={(e) => handleClick(slide._id, e)}
              onTouchStart={(e) => handleClick(slide._id, e)}
            >
              {/* Low-res blurred placeholder */}
              {slide.lowResImg && !loadedSlides[slide._id] && (
                <img
                  src={slide.lowResImg}
                  alt="placeholder"
                  className="blur-placeholder"
                  draggable={false}
                />
              )}

              {/* Main poster */}
              <img
                src={slide.previewImg}
                alt={slide.title || "Preview"}
                draggable={false}
                onLoad={() => handleImageLoad(slide._id)}
                className={loadedSlides[slide._id] ? 'loaded' : 'loading'}
              />

              {/* Dark overlay while loading */}
              {!loadedSlides[slide._id] && <div className="loading-overlay" />}

              {/* Tap indicator */}
              {tapPosition && (
                <div
                  className="tap-indicator"
                  style={{ left: tapPosition.x, top: tapPosition.y }}
                />
              )}
            </div>
          </SwiperSlide>
        ))}
      </Swiper>

      {/* Custom hand cursor */}
      <div
        className="custom-cursor-hand"
        style={{ transform: `translate3d(${cursorPos.x}px, ${cursorPos.y}px, 0)` }}
      />
    </>
  );
}

export default MovieSwiper;
