import './modal.css';

function Modal({ movie, status, toggleModal }) {
  if (!movie) return null; // Optional: prevents errors if movie is undefined

  return (
    <div className={`movieModal ${status ? 'active' : ''}`}>
      <button className="modalClose" onClick={toggleModal}>
        <ion-icon name="close-outline"></ion-icon>
      </button>
      <iframe
        width="1280"
        height="720"
        src={movie.video}
        title={`${movie.title} | Official Movie`}
        frameBorder="0"
        allow="accelerometer; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
        allowFullScreen
      ></iframe>
    </div>
  );
}

export default Modal;
