import React, { useState } from 'react';
import './movieSwiper.css';
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import 'swiper/css/pagination';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, EffectCoverflow } from 'swiper/modules';

function MovieSwiper({ slides, slideChange }) {
  const [tapPosition, setTapPosition] = useState(null);

  // Handle click/tap on slide
  const handleSlideClick = (slideId, e) => {
    slideChange(slideId);

    // Show temporary tap indicator for touch devices
    if (e.touches || e.type === 'touchstart') {
      const rect = e.currentTarget.getBoundingClientRect();
      setTapPosition({ x: e.clientX - rect.left, y: e.clientY - rect.top });
      setTimeout(() => setTapPosition(null), 500); // hide after 0.5s
    }
  };

  return (
    <Swiper
      effect="coverflow"
      grabCursor={true}
      centeredSlides={true}
      slidesPerView={'auto'}
      loop={true}
      autoplay={{
        delay: 2500,
        disableOnInteraction: false,
        pauseOnMouseEnter: true,
      }}
      coverflowEffect={{
        rotate: 40,
        stretch: 0,
        depth: 120,
        modifier: 1,
        slideShadows: true,
      }}
      modules={[Autoplay, EffectCoverflow]}
      className="movieSwiper"
      allowTouchMove={true}
      simulateTouch={true}
      touchStartPreventDefault={false}
      shortSwipes={true}
      longSwipes={true}
      touchRatio={1}
      threshold={5}
    >
      {slides.map((slide) => (
        <SwiperSlide 
          key={slide._id}
          onClick={(e) => handleSlideClick(slide._id, e)}
          onTouchStart={(e) => handleSlideClick(slide._id, e)}
        >
          <div className="slide-content">
            <img
              src={slide.previewImg}
              alt="Preview"
              draggable={false}
            />
            {/* Mobile tap indicator */}
            {tapPosition && (
              <div
                className="tap-indicator"
                style={{ left: tapPosition.x, top: tapPosition.y }}
              />
            )}
          </div>
        </SwiperSlide>
      ))}
    </Swiper>
  );
}

export default MovieSwiper;
