import React, { useState, useEffect } from 'react';
import './movieSwiper.css';
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import 'swiper/css/pagination';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, EffectCoverflow } from 'swiper/modules';

function MovieSwiper({ slides, slideChange }) {
    const [isMobile, setIsMobile] = useState(window.innerWidth <= 1037);

    useEffect(() => {
        const handleResize = () => {
            setIsMobile(window.innerWidth <= 1037);
        };
        
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    // Reduced settings for mobile
    const mobileSettings = {
        effect: "slide", // Use slide instead of coverflow on mobile
        grabCursor: true,
        centeredSlides: true,
        slidesPerView: 'auto',
        spaceBetween: 10,
        loop: true,
        speed: 300, // Faster transitions
        autoplay: {
            delay: 3000, // Slower autoplay
            disableOnInteraction: false,
        },
        modules: [Autoplay],
        className: "movieSwiper mobile"
    };

    // Full settings for desktop
    const desktopSettings = {
        effect: "coverflow",
        grabCursor: true,
        centeredSlides: true,
        slidesPerView: 'auto',
        loop: true,
        speed: 600,
        autoplay: {
            delay: 2500,
            disableOnInteraction: false,
        },
        coverflowEffect: {
            rotate: 50,
            stretch: 0,
            depth: 100,
            modifier: 1,
            slideShadows: true,
        },
        modules: [Autoplay, EffectCoverflow],
        className: "movieSwiper desktop"
    };

    const settings = isMobile ? mobileSettings : desktopSettings;

    return (
        <Swiper {...settings}>
            {slides.map(slide => (
                <SwiperSlide key={slide._id}>
                    <img
                        src={slide.previewImg}
                        alt="Preview"
                        onClick={() => slideChange(slide._id)}
                        loading="lazy" // Lazy load images
                    />
                </SwiperSlide>
            ))}
        </Swiper>
    );
}

export default MovieSwiper;
