import React, { useState, useEffect } from 'react';
import './movieSwiper.css';
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import 'swiper/css/free-mode';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, EffectCoverflow, FreeMode } from 'swiper/modules';

function MovieSwiper({ slides, slideChange }) {
  const [allowTouch, setAllowTouch] = useState(true);

  useEffect(() => {
    // Detect desktop vs mobile
    const isDesktop = window.matchMedia('(hover: hover) and (pointer: fine)').matches;
    setAllowTouch(!isDesktop);
  }, []);

  return (
    <div className="movieSwiper">
      <Swiper
        effect="coverflow"
        grabCursor={true}
        centeredSlides={true}
        slidesPerView="auto"
        loop={true}
        autoplay={{ delay: 2500, disableOnInteraction: false }}
        coverflowEffect={{
          rotate: 30,
          stretch: 0,
          depth: 100,
          modifier: 1,
          slideShadows: true,
        }}
        freeMode={true}
        modules={[Autoplay, EffectCoverflow, FreeMode]}
        onSlideChange={(swiper) => slideChange && slideChange(swiper.realIndex)}
        allowTouchMove={allowTouch}  // ✅ disables swipe on desktop
      >
        {slides.map((slide, index) => (
          <SwiperSlide key={index}>
            <div className="slide-content">
              <img src={slide.image} alt={slide.title || `Slide ${index}`} />
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
}

export default MovieSwiper;
