import React, { useState } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, EffectCoverflow } from 'swiper/modules';
import PlayBtn from './PlayBtn';
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import 'swiper/css/pagination';
import './movieSwiper.css';

function MovieSwiper({ slides }) {
  const [moviePlaying, setMoviePlaying] = useState(false);

  return (
    <div className={`movieSwiper ${moviePlaying ? 'disabled' : ''}`}>
      <Swiper
        modules={[Autoplay, EffectCoverflow]}
        effect="coverflow"
        grabCursor
        centeredSlides
        slidesPerView="auto"
        autoplay={moviePlaying ? false : { delay: 3000 }} // stop autoplay when movie is playing
        coverflowEffect={{
          rotate: 50,
          stretch: 0,
          depth: 100,
          modifier: 1,
          slideShadows: true,
        }}
      >
        {slides.map((movie, index) => (
          <SwiperSlide key={index}>
            <img src={movie.poster} alt={movie.title} />
            <PlayBtn
              movie={movie}
              onPlayChange={(state) => setMoviePlaying(state)} // ðŸ”¥ sync state
            />
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
}

export default MovieSwiper;
