import React, { useState } from 'react';
import { slide as Menu } from 'react-burger-menu';
import NavListItem from '../components/NavListItem';
import navListData from '../data/navListData';
import './header.css';

function Header({ scroll }) {
  const [active, setActive] = useState(false); // Change to boolean state for menu visibility
  const [icon, setIcon] = useState(false); // Change to boolean state for icon toggling

  // Function to toggle the menu state
  const toggleMenu = () => {
    setActive(!active);

    // Toggle icon state
    setIcon(!icon);
  };

  return (
    <header className={`${scroll > 100 ? 'scrolled' : undefined}`}>
      <a href="/" className="logo">
        Stella Belessi
      </a>
      
      {/* Burger Menu */}
      <Menu isOpen={active} className="burger-menu">
        <ul className="nav__menu">
          <li className="nav__item">
            <a href="#" className="nav__link">
              <i className="bi bi-house"></i>
            </a>
          </li>
          {navListData.map(nav => (
            <NavListItem key={nav._id} nav={nav} />
          ))}
        </ul>
      </Menu>

      {/* Burger Menu Toggler */}
      <div onClick={toggleMenu} className={`nav__toggler ${icon ? 'toggle' : ''}`}>
        <div className="line1"></div>
        <div className="line2"></div>
        <div className="line3"></div>
      </div>
    </header>
  );
}

export default Header;
