import React, { useState, useCallback } from 'react';
import './movieSwiper.css';

// Swiper styles
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import 'swiper/css/free-mode';

// Swiper core
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, EffectCoverflow, FreeMode, Keyboard } from 'swiper/modules';

function MovieSwiper({ slides, slideChange }) {
  const [tapPosition, setTapPosition] = useState(null);

  // Optimized click/touch handler
  const handleClick = useCallback((slideId, e) => {
    slideChange(slideId);

    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.type.startsWith('touch') ? e.touches[0].clientX - rect.left : e.clientX - rect.left;
    const y = e.type.startsWith('touch') ? e.touches[0].clientY - rect.top : e.clientY - rect.top;

    setTapPosition({ x, y });
  }, [slideChange]);

  return (
    <Swiper
      modules={[EffectCoverflow, Autoplay, FreeMode, Keyboard]}
      effect="coverflow"
      grabCursor
      centeredSlides
      slidesPerView="auto"
      loop
      freeMode={{ enabled: true, momentum: true }}
      autoplay={{ delay: 2500, disableOnInteraction: false, pauseOnMouseEnter: true }}
      coverflowEffect={{ rotate: 50, stretch: 0, depth: 100, modifier: 1, slideShadows: true }}
      keyboard={{ enabled: true }}
      breakpoints={{
        375: { slidesPerView: 1.2, spaceBetween: 10 },
        768: { slidesPerView: 2, spaceBetween: 20 },
        1037: { slidesPerView: 3, spaceBetween: 30 },
        1440: { slidesPerView: 4, spaceBetween: 40 },
      }}
      className="movieSwiper"
    >
      {slides.map(slide => (
        <SwiperSlide key={slide._id} role="button" aria-label="Movie slide">
          <div className="slide-content">
            <img
              src={slide.previewImg}
              alt="Preview"
              draggable={false}
              loading="lazy"
              onClick={(e) => handleClick(slide._id, e)}
              onTouchStart={(e) => handleClick(slide._id, e)}
            />
            {tapPosition && (
              <div
                className="tap-indicator"
                style={{ left: tapPosition.x, top: tapPosition.y }}
                onAnimationEnd={() => setTapPosition(null)}
              />
            )}
          </div>
        </SwiperSlide>
      ))}
      <div className="swipe-hint" />
    </Swiper>
  );
}

export default MovieSwiper;
