import React, { useState, useCallback, useRef } from 'react';
import './movieSwiper.css';
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import 'swiper/css/free-mode';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, EffectCoverflow, FreeMode } from 'swiper/modules';

function MovieSwiper({ slides, slideChange }) {
  const [tapPosition, setTapPosition] = useState(null);
  const [hoverPosition, setHoverPosition] = useState(null);
  const swiperRef = useRef(null);

  // Track if user is dragging
  const isDragging = useRef(false);

  // Mobile / click handler
  const handleClick = useCallback((slideId, e) => {
    // Don't trigger click if user was dragging
    if (isDragging.current) {
      isDragging.current = false;
      return;
    }
    
    slideChange(slideId);
    const rect = e.currentTarget.getBoundingClientRect();
    let x, y;
    if (e.type.startsWith("touch")) {
      const touch = e.touches[0];
      x = touch.clientX - rect.left;
      y = touch.clientY - rect.top;
    } else {
      x = e.clientX - rect.left;
      y = e.clientY - rect.top;
    }
    setTapPosition({ x, y });
    setTimeout(() => setTapPosition(null), 500);
  }, [slideChange]);

  // Desktop hover handler
  const handleHover = useCallback((e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    setHoverPosition({ x, y });
    setTimeout(() => setHoverPosition(null), 500);
  }, []);

  // Handle swiper events for cursor changes
  const handleSwiperInit = useCallback((swiper) => {
    swiperRef.current = swiper;
    
    // Add dragging class on touch/mouse start
    swiper.on('touchStart', () => {
      isDragging.current = false;
      const wrapper = swiper.wrapperEl;
      if (wrapper) wrapper.classList.add('swiper-dragging');
    });
    
    // Detect actual dragging movement
    swiper.on('sliderMove', () => {
      isDragging.current = true;
    });
    
    // Remove dragging class on touch/mouse end
    swiper.on('touchEnd', () => {
      const wrapper = swiper.wrapperEl;
      if (wrapper) wrapper.classList.remove('swiper-dragging');
      // Reset dragging flag after a short delay
      setTimeout(() => {
        isDragging.current = false;
      }, 100);
    });
  }, []);

  return (
    <Swiper
      modules={[EffectCoverflow, Autoplay, FreeMode]}
      effect="coverflow"
      grabCursor={false}  // CHANGED: Set to false to use custom cursors
      centeredSlides={true}
      slidesPerView="auto"
      loop={true}
      freeMode={{ enabled: true, momentum: true }}
      autoplay={{ delay: 2500, disableOnInteraction: false }}
      coverflowEffect={{ rotate: 50, stretch: 0, depth: 100, modifier: 1, slideShadows: true }}
      touchRatio={2.5}
      threshold={5}
      resistance={true}
      resistanceRatio={0.85}
      className="movieSwiper"
      onSwiper={handleSwiperInit}  // ADDED: Initialize swiper events
    >
      {slides.map(slide => (
        <SwiperSlide key={slide._id}>
          <div
            className="slide-content"
            onClick={(e) => handleClick(slide._id, e)}
            onMouseMove={handleHover}
          >
            <img
              src={slide.previewImg}
              alt={slide.title || 'Movie preview'}
              draggable={false}
              loading="lazy"
            />
            {tapPosition && (
              <div
                className="tap-indicator"
                style={{ left: tapPosition.x, top: tapPosition.y }}
              />
            )}
            {hoverPosition && (
              <div
                className="hover-indicator"
                style={{ left: hoverPosition.x, top: hoverPosition.y }}
              />
            )}
          </div>
        </SwiperSlide>
      ))}
    </Swiper>
  );
}

export default MovieSwiper;
