import React, { useState, useEffect } from 'react';
import './header.css';
import NavListItem from '../components/NavListItem';
import navListData from '../data/navListData';

function Header({ scroll }) {
  const [navList, setNavList] = useState(navListData);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  // Handle menu item click
  const handleNavOnClick = id => {
    const newNavList = navList.map(nav => ({
      ...nav,
      active: nav._id === id
    }));
    setNavList(newNavList);
  };

  // Toggle menu open/close
  const navToggle = () => setIsMenuOpen(prev => !prev);

  const closeMenu = () => setIsMenuOpen(false);

  // Lock scroll when menu is open
  useEffect(() => {
    if (isMenuOpen) {
      const scrollY = window.scrollY;
      document.body.style.overflow = 'hidden';
      document.body.style.position = 'fixed';
      document.body.style.top = `-${scrollY}px`;
      document.body.style.width = '100%';
      document.body.style.touchAction = 'none';
      document.body.setAttribute('data-scroll-y', scrollY);
    } else {
      const scrollY = document.body.getAttribute('data-scroll-y');
      document.body.style.overflow = '';
      document.body.style.position = '';
      document.body.style.top = '';
      document.body.style.width = '';
      document.body.style.touchAction = '';
      if (scrollY) {
        window.scrollTo(0, parseInt(scrollY));
        document.body.removeAttribute('data-scroll-y');
      }
    }
  }, [isMenuOpen]);

  // Close menu on Escape key
  useEffect(() => {
    const handleEscape = e => {
      if (e.key === 'Escape' && isMenuOpen) closeMenu();
    };
    document.addEventListener('keydown', handleEscape);
    return () => document.removeEventListener('keydown', handleEscape);
  }, [isMenuOpen]);

  // Close menu when clicking backdrop
  const handleBackdropClick = e => {
    if (e.target.classList.contains('nav__menu') && isMenuOpen) {
      closeMenu();
    }
  };

  return (
    <header className={`${scroll > 100 ? 'scrolled' : ''}`}>
      <a href="/" className="logo">
        Stella Belessi
      </a>

      <ul
        className={`nav__menu ${isMenuOpen ? 'nav__active' : ''}`}
        onClick={handleBackdropClick}
      >
        {navList.map(nav => (
          <NavListItem
            key={nav._id}
            nav={nav}
            onClick={closeMenu}
            navOnClick={handleNavOnClick}
          />
        ))}
      </ul>

      {/* Hamburger / X button */}
      <div onClick={navToggle} className={`nav__toggler ${isMenuOpen ? 'toggle' : ''}`}>
        <div className="line1"></div>
        <div className="line2"></div>
        <div className="line3"></div>
      </div>
    </header>
  );
}

export default Header;
