import React, { useState } from 'react';
import './movieSwiper.css';
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import 'swiper/css/pagination';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, EffectCoverflow } from 'swiper/modules';

function MovieSwiper({ slides, slideChange }) {
  const [touchPoint, setTouchPoint] = useState(null);

  return (
    <Swiper
      effect="coverflow"
      grabCursor={true}
      centeredSlides={true}
      slidesPerView={'auto'}
      loop={true}
      autoplay={{
        delay: 2500,
        disableOnInteraction: false,
        pauseOnMouseEnter: true,
      }}
      coverflowEffect={{
        rotate: 40,
        stretch: 0,
        depth: 120,
        modifier: 1,
        slideShadows: true,
      }}
      modules={[Autoplay, EffectCoverflow]}
      className="movieSwiper"
      allowTouchMove={true}
      simulateTouch={true}
      touchStartPreventDefault={false}
      shortSwipes={true}
      longSwipes={true}
      touchRatio={1}
      threshold={5}

      // Debug events
      onSwiper={(swiper) => console.log('Swiper instance:', swiper)}
      onSlideChange={() => console.log('Slide Changed')}
      onTouchStart={(e) => console.log('Touch Start', e)}
      onTouchMove={(e) => console.log('Touch Move', e)}
      onTouchEnd={(e) => console.log('Touch End', e)}
      onSlideClick={(swiper, e) => {
        const index = swiper.clickedIndex;
        if (index != null) {
          console.log('Slide clicked index:', index);
          slideChange(slides[index]._id);

          // Show handpoint image at touch
          const rect = swiper.slides[index].getBoundingClientRect();
          setTouchPoint({
            x: e.clientX - rect.left,
            y: e.clientY - rect.top,
            slideIndex: index
          });
          setTimeout(() => setTouchPoint(null), 600);
        }
      }}
    >
      {slides.map((slide, idx) => (
        <SwiperSlide key={slide._id}>
          <div className="slide-content">
            <img
              src={slide.previewImg}
              alt="Preview"
              draggable={false}
            />

            {/* Show handpoint indicator */}
            {touchPoint && touchPoint.slideIndex === idx && (
              <img
                src="/src/handpointing.png"
                className="handpoint-indicator"
                style={{
                  left: touchPoint.x,
                  top: touchPoint.y
                }}
                alt="Handpoint"
              />
            )}
          </div>
        </SwiperSlide>
      ))}
    </Swiper>
  );
}

export default MovieSwiper;
